    private void codifActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_codifActionPerformed
        area1.setText(null);

        String frase2 = frase.getText();
        int carac;
        int numCod = Integer.valueOf(numUser.getText());
        char letra;
        for (carac = 0; carac <= frase2.length() - 1; carac++) {
            letra = frase2.charAt(carac);
            letra += numCod;
            if (letra >= 'z') {
                letra -= 26;
            }
            
            
            area1.append(String.valueOf(letra));            
        }
        
    }//GEN-LAST:event_codifActionPerformed

